divisao = 10 / 2.2 #retorna float
print('Divisão', divisao)

divisao_inteira = 10 // 2.2 #retorna um número redondo, ignorando os decimais
print('Divisão', divisao_inteira)

exponenciacao = 2 ** 10
print('Exponenciação', exponenciacao)

modulo = 25 % 5 #resto da divisão
print('Módulo', modulo)

print(10 % 8)
print(10 % 8 == 0)
print(16 % 8 == 0)
print(10 % 2 == 0)
print(15 % 2 == 0)


